<?php$loggedin_user = elgg_get_logged_in_user_entity();$username = $loggedin_user->username;?><?php$title = elgg_echo('Quick links');if(elgg_is_logged_in()){	$river_body = elgg_view('activity/quicklinks', array('user' => $loggedin_user));	echo elgg_view_module('featured', $title, $river_body);}?><?php$title = elgg_echo('Navigation');$menus = array(	'My profile' => 'profile/'.$loggedin_user->username,				'My causes' => 'causes/mine/'.$loggedin_user->guid,				'Konnected causes' => 'causes/konnected/',				'Participating events' => 'event/myticket/'.$loggedin_user->guid,				'My Non-Profits' => 'groups/member/'.$username,				'Non-Profits I own' => 'groups/owner/'.$username,			);$linkshtml = "<ul class='elgg-menu elgg-menu-page elgg-menu-page-default'>";			foreach ($menus as $k => $v){	$linkshtml .= "<li>".elgg_view('output/url' , array('href' => elgg_get_site_url().$v, 'text' => $k, 'is_safe' => true))."</li>"; }		$linkshtml .= "</ul>";			echo elgg_view_module('featured', $title, $linkshtml);			?><?php//	echo elgg_view_module('featured', elgg_echo("kme:advertisement"), elgg_view('ads/slot3'));$title = 'My friends';$friend = elgg_list_entities_from_relationship(array(            'type' => 'user',            "limit" => 12,            'relationship' => 'friend',            'relationship_guid' => $loggedin_user->guid,            'inverse_relationship' => false,            'full_view' => false,            'pagination' => false,            'list_type' => 'gallery',            'gallery_class' => 'elgg-gallery-users',			'offset' => 0,        ));if ($friend) {	$river_body = $friend;	$river_body .= '<p class="center"><a href="'.SITE_URL.'friends/'.$username.'"><b>'.elgg_echo('more').'</b></a></p>';}else {	$river_body = 'You have no friends.';	$river_body .= '<p class="center"><a href="'.SITE_URL.'members"><b>'.'Add more friends'.'</b></a></p>';}echo elgg_view_module('featured', $title, $river_body);?>	<?php	echo elgg_view_module('featured',  elgg_echo('tagcloud'),  elgg_view('page/elements/tagcloud_block', array('nowrap' => true)));?>